script:
frappe.ui.form.on("Supplier Quotation",{
	refresh: function(frm) {
frm.add_custom_button(__("Create Pricing Rule for Quotation"), function(foo) {

		frappe.call({
			method:"velometro.velometro.supplier_quotation.add_pricing_rules",
			args: {
				mquotation: cur_frm.doc.name,

			}, 
			callback: function(r) { 
				frm.reload_doc();

			}
		});
});
	}
});

frappe.ui.form.on("Purchase Order",{
	refresh: function(frm) {
frm.add_custom_button(__("Load Attachments"), function(foo) {

		frappe.call({
			method:"velometro.velometro.purchase_order.attach_all_docs",
			args: {
				document: cur_frm.doc,

			}, 
			callback: function(r) { 
				frm.reload_doc();

			}
		});
});
	}
});

frappe.ui.form.on("Purchase Order",{
	refresh: function(frm) {
frm.add_custom_button(__("Create ZIP"), function(foo) {

		frappe.call({
			method:"velometro.velometro.purchase_order.zip_attachments",
			args: {
				document: cur_frm.doc
			}, 
			callback: function(r) { 
				frm.reload_doc();

			}
		});
});
	}
});

frappe.ui.form.on("Request for Quotation",{
	refresh: function(frm) {
frm.add_custom_button(__("Load Attachments"), function(foo) {

		frappe.call({
			method:"velometro.velometro.purchase_order.attach_all_docs",
			args: {
				document: cur_frm.doc,

			}, 
			callback: function(r) { 
				frm.reload_doc();

			}
		});
});
	}
});

frappe.ui.form.on("Item",{
	refresh: function(frm) {
frm.add_custom_button(__("Copy Pricing from Previous Revision"), function(foo) {

		frappe.call({
			method:"velometro.velometro.supplier_quotation.copy_pricing_rule_from_previous_revision",
			args: {
				"base_item_code": cur_frm.doc.variant_of,
				"current_rev": cur_frm.doc.attributes[0].attribute_value
			}, 
			callback: function(r) { 
				frm.reload_doc();

			}
		});
});
	}
});
